import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { CrudService } from '../service/crud.service';

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css']
})
export class ProductsComponent implements OnInit {
  // @Output() addtomycart: EventEmitter<any> = new EventEmitter()

  products!: any;

  constructor(private crud: CrudService) {

    if(JSON.parse(sessionStorage.getItem('mycart') || '[]').length == 0){
      let cart: any = []
      sessionStorage.setItem('mycart', JSON.stringify(cart))
      console.log("exec success")
    }
  }

  ngOnInit(): void {
    this.getProducts()

  }

  getProducts() {
    this.crud.getProducts().subscribe(res => {
      this.products = res,
        console.log('done')
    })

  }
  // valueChanged: (data: number) => void = (d: number) => { };

  addtocart(id: string) {
    console.log("$$$$$$$$$$$$$$$$$$$$$$$$$$$$4")
    // this.addtomycart.emit(id)
    let mycart = JSON.parse(sessionStorage.getItem('mycart') || '[]')
    console.log('Current Cart: ', mycart)
    mycart.push(id)
    console.log('Updated cart: ', mycart)
    sessionStorage.setItem('mycart', JSON.stringify(mycart))
  }

}


